import { FETCH_BLOGS } from './action';

const initialState ={
    isMobile: window.innerWidth < 768,
    isSidebarOpen: false,
}
const blogReducer = (state = initialState, action) =>{
    switch(action.type) {
        case FETCH_BLOGS:
            return {
                ...state,
                isMobile: action.value,
            }
        default:
            return state
    }

}

export default blogReducer;
