import React,{PureComponent} from 'react';
import OwlCarousel from "react-owl-carousel3";
import Icofont from "react-icofont";
import PropTypes from "prop-types";
import { Link } from 'react-router-dom';
import VisibilitySensor from "react-visibility-sensor";
import { Navbar, Container, Nav } from "react-bootstrap";

export default class  Banner extends PureComponent {

    constructor(props){
        super(props)
    }

    render(){
        const banneronedata = this.props.banneronesData.map(
            (bannerone, index) => (
                <div className={bannerone.BgClass} key={index}>
                    <div className="diplay-table">
                        <div className="display-table-cell">
                            <VisibilitySensor>
                                {({ isVisible }) => (
                                <div className="container">
                                    <div className="row">
                                        <div className="main-banner-text">
                                            <div className="col-lg-7">
                                                <span
                                                    className={
                                                        isVisible
                                                            ? "hero-text animated fadeInDown slow opacityOne"
                                                            : "opacityZero"
                                                    }
                                                >
                                                    {bannerone.TopTitle}
                                                </span>

                                                <h1
                                                    className={
                                                        isVisible
                                                            ? "animated fadeInDown slow opacityOne"
                                                            : "opacityZero"
                                                    }
                                                >
                                                    {bannerone.Title}
                                                </h1>
                                                <p
                                                    className={
                                                        isVisible
                                                            ? "animated fadeInDown slow opacityOne"
                                                            : "opacityZero"
                                                    }
                                                >
                                                    {bannerone.Content}
                                                </p>
                                                <div className="center-wrap">
                                                    {/*<Link to={bannerone.BtnLink} className="btn-a">*/}
                                                        {/*<div className="button">*/}

                                                        {/*{bannerone.BtnName} <Icofont icon="icofont-long-arrow-right" />*/}
                                                        {/*<div className="mask" /></div>*/}
                                                    {/*</Link>*/}
                                                    <Nav.Item>
                                                        <Link
                                                            activeclass="active"
                                                            to="services"
                                                            spy={true}
                                                            smooth={true}
                                                            offset={-200}
                                                            duration={800}
                                                            className="nav-link"
                                                            onClick={this.closeNavbar}
                                                        >
                                                            Khóa Học
                                                        </Link>
                                                    </Nav.Item>

                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            )}
                            </VisibilitySensor>
                        </div>
                    </div>
                </div>
            )
        );
        //BannerOne loop END
        
        //Thumbs loop Start
        const thumbdata = this.props.thumbsData.map((thumb, index) => (
            <div className="owl-thumb-item" key={index}>
                <Icofont icon={thumb.ThumbIcon} />
                <h3>{thumb.ThumbTitle}</h3>
                <p>{thumb.ThumbContent}</p>
            </div>
            )
        );
        return(
            <React.Fragment>
            <div id="home" className="hompage-slides-wrapper">
                <OwlCarousel
                    className="owl-theme homepage-slides"
                    items={1}
                    touchDrag={false}
                    margin={0}
                    mouseDrag={false}
                    smartSpeed={1000}
                    dotData={true}
                    dotsContainer={".owl-thumbs"}
                >
                    {banneronedata}
                </OwlCarousel>

                <div className="owl-thumbs">
                    {thumbdata}
                </div>
            </div>
        </React.Fragment>
        )
    }

}
//Props Types
Banner.propTypes = {
    banneronesData: PropTypes.array,
    thumbsData: PropTypes.array
};

//Default Props
Banner.defaultProps = {
    banneronesData: [
        {
            BgClass: "single-slider-item slide-bg-1",
            TopTitle: "Sáng Tạo & Đam Mê",
            Title: "Thiết Kế Đồ Họa  Anh Em",
            Content:
                ".Cam Kết Đào Tạo ,Tạo ra Giá Trị Riêng Cho Bạn",
            BtnLink:  "services",
            BtnName: "Hành Động Thôi"
        },
        {
            BgClass: "single-slider-item slide-bg-2",
            TopTitle: "Sáng Tạo & Đam Mê",
            Title: "Thiết Kế Đồ Họa  Anh Em",
            Content:
                ".Cam Kết Đào Tạo ,Tạo ra Giá Trị Riêng Cho Bạn",
            BtnLink:  "services",
            BtnName: "Hành Động Thôi"
        },
        {
            BgClass: "single-slider-item slide-bg-3",
            TopTitle: "Sáng Tạo & Đam Mê",
            Title: "Thiết Kế Đồ Họa  Anh Em",
            Content:
                ".Cam Kết Đào Tạo ,Tạo ra Giá Trị Riêng Cho Bạn",
            BtnLink:  "services",
            BtnName: "Hành Động Thôi"
        }
    ],
    thumbsData: [
        {
            ThumbIcon: "icofont-rocket-alt-1",
            ThumbTitle: "Cam Kết",
            ThumbContent: "Bạn Sẽ được đào tạo bởi những người thầy giỏi có chuyên môm cao và tận tâm trong công việc.",
        },
        {
            ThumbIcon: "icofont-box",
            ThumbTitle: "Thực Hành",
            ThumbContent: "Được thực hành 90% trong quá trình học với sự chỉ bảo của giảng viên.",
        },
        {
            ThumbIcon: "icofont-live-support",
            ThumbTitle: "Hỗ Trợ",
            ThumbContent: "Hỗ trợ nơi ở cho những bạn có hoàn cảnh đặc biệt khó khăn và ở xa nhà.",
        },
    ]
};
