    blogs : []
}

function blogReducer(state = initialState, action) {
    return dispatch => {
        dispatch(FETCH_BLOGS_SUCCESS());
        fetch('https://nodejssalesforce.herokuapp.com/blogs')
            .then(res => res.json())
            .then(res => {
                console.log('res' , res);
                if(res.error) {
                    throw(res.error);
                }
                // dispatch(FETCH_BLOGS_SUCCESS(res.products));
                return res;
            })
            .catch(error => {
                // dispatch(FETCH_BLOGS_ERROR(error));
                throw (error);
            })
    }
}
