import React, {PureComponent} from 'react';
import PropTypes from "prop-types";
import Icofont from 'react-icofont';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';
import ScrollAnimation from 'react-animate-on-scroll';
import GoogleMapReact from 'google-map-react';
import ReactGoogleMapLoader from "react-google-maps-loader"
import ReactGoogleMap from "react-google-map"
const CustomComponent = ({ text }) => <div><img src="http://ruralshores.com/assets/marker-icon.png" alt="map" /></div>;

export default class Contact extends PureComponent {
    constructor(props) {
        super(props);

        this.state = {
            photoIndex: 0,
            isOpen: false
        };
    }

    render() {
        return (
            <React.Fragment>
                <section id="contact" className="contact-area ptb-100">
                    <div className="container">
                        <div className="row">
                            <div className="col-lg-12 text-center">
                                <ScrollAnimation animateIn="fadeInUp">
                                    <div className="section-title">
                                        <h2>{this.props.sectionTitle}</h2>
                                        <p>{this.props.sectionDescription}</p>
                                        <span className="section-title-bg">{this.props.SectionbgTitle}</span>
                                    </div>
                                </ScrollAnimation>
                        </div>
                        </div>

                        <div className="row">
                            <div className="col-lg-4 col-md-4">
                                <div className="address-area">
                                    <div className="addess">
                                        <Icofont icon="icofont-google-map"/>
                                        <h4>{this.props.AddTitle}</h4>
                                        <p>{this.props.Address}</p>
                                    </div>
                                    <div className="email">
                                        <Icofont icon="icofont-email"/>
                                        <h4>{this.props.EmailTitle}</h4>
                                        <p>{this.props.Email}</p>
                                    </div>
                                    <div className="phone">
                                        <Icofont icon="icofont-phone"/>
                                        <h4>{this.props.PhoneTitle}</h4>
                                        <p>{this.props.Phone}</p>
                                    </div>
                                </div>
                            </div>

                            <div className="col-lg-8 col-md-8">
                                {/* <div id="map">
                                    <div style={{ height: '100%', width: '100%' }}>
                                        <GoogleMapReact
                                            bootstrapURLKeys={{ key: 'WcWMkD6Jp36oz0afEHbug9LtfzfROa-FOxqs1OGCDJg' }}
                                            defaultCenter={this.props.center}
                                            defaultZoom={this.props.zoom}
                                        >
                                            <CustomComponent
                                                lat={59.955413}
                                                lng={30.337844}
                                                text="Marker"
                                            />
                                        </GoogleMapReact>
                                    </div>
                                </div> */}
                                <ReactGoogleMapLoader
                                    params={{
                                        key: "AIzaSyCI3cDduwloUnVSfREo-6wuRYTMjOHcQjc",
                                        libraries: "places,geometry",
                                    }}
                                    render={googleMaps =>
                                        googleMaps && (
                                        <div style={{height: "300px"}}>
                                            <ReactGoogleMap
                                            googleMaps={googleMaps}
                                            center={{lat: 43.604363, lng: 1.443363}}
                                            zoom={8}
                                            />
                                        </div>
                                        )}
                                />
                            </div>

                        </div>
                    </div>
                </section>
            </React.Fragment>
        );
    }
}
//Props Types
Contact.propTypes = {
    SectionbgTitle: PropTypes.string,
    sectionTitle: PropTypes.string,
    sectionDescription: PropTypes.string,
    AddTitle: PropTypes.string,
    Address: PropTypes.string,
    EmailTitle: PropTypes.string,
    Email: PropTypes.string,
    PhoneTitle: PropTypes.string,
    Phone: PropTypes.string,
};

//Default Props
Contact.defaultProps = {
    SectionbgTitle: "Contact",
    sectionTitle: "Send Us Your Feedback",
    sectionDescription: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Maecenas ac augue at erat hendrerit dictum. Praesent porta, purus eget sagittis imperdiet.",
    AddTitle: "Address",
    Address: "2750 Quadra Street Victoria, Canada.",
    EmailTitle: "Email",
    Email: "roxy@gmail.com",
    PhoneTitle: "Phone",
    Phone: "+1-325-555-0156",
};
