import React, {PureComponent} from 'react';
import PropTypes from "prop-types";
import Icofont from 'react-icofont';
import { Link } from 'react-router-dom'
import OwlCarousel from 'react-owl-carousel3';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';
import ScrollAnimation from 'react-animate-on-scroll';
import './team.css';

export default class Team extends PureComponent {
    constructor(props) {
        super(props);

        this.state = {
            photoIndex: 0,
            isOpen: false
        };
    }

    render() {
        //Team loop start
        const teamdata = this.props.teamsData.map((team, index) => (
            <div className="team-box" key={index}>
                <img src={team.Image} alt="Description" />
                <div className="box-content">
                    <div className="box-inner-content">
                        <h3 className="title">{team.Name}</h3>
                        <span className="post">{team.Profession}</span>
                        <ul className="icon">
                            {/*<li><Link to={team.facebookLink}><Icofont icon="icofont-facebook" /></Link></li>*/}
                            <li><a href={this.props.teamsData[index].facebookLink} rel="noopener noreferrer" target="_blank"><Icofont icon="icofont-facebook" /></a></li>
                            <li><Link to={team.linkedinLink}><Icofont icon="icofont-linkedin" /></Link></li>
                            <li><Link to={team.twitterLink}><Icofont icon="icofont-twitter" /></Link></li>
                        </ul>
                    </div>
                </div>
            </div>
        ));
        //Team loop END
        return (
            <React.Fragment>
                <section id="team" className="our-team ptb-100">
                    <div className="container">
                        <div className="row">
                            <div className="col-lg-12 text-center">
                                <ScrollAnimation animateIn="fadeInUp">
                                    <div className="section-title">
                                        <h2>{this.props.sectionTitle}</h2>
                                        <p>{this.props.sectionDescription}</p>
                                        <span className="section-title-bg">{this.props.SectionbgTitle}</span>
                                    </div>
                                </ScrollAnimation>
                            </div>
                        </div>

                        <div className="row">
                            <OwlCarousel
                                className="owl-theme team-slides"
                                dots= {false}
                                autoplay= {true}
                                loop= {true}
                                margin={30}
                                nav={true}
                                smartSpeed= {1000}
                                autoplayHoverPause= {true}
                                navText= {[
                                    "<i class='icofont-arrow-left'></i>",
                                    "<i class='icofont-arrow-right'></i>"
                                ]}
                                responsive={{
                                    0: { items: 1 },
                                    768: {
                                        items: 2
                                    },
                                    1024: {
                                        items: 3
                                    },
                                    1200: {
                                        items: 3
                                    }
                                }}
                            >
                                {teamdata}
                            </OwlCarousel>
                        </div>
                    </div>
                </section>
            </React.Fragment>
        );
    }
}
//Props Types
//Props Types
Team.propTypes = {
    SectionbgTitle: PropTypes.string,
    sectionTitle: PropTypes.string,
    sectionDescription: PropTypes.string,
    teamsData: PropTypes.array
};

//Default Props
Team.defaultProps = {
    SectionbgTitle: "Giảng Viên",
    sectionTitle: "Đội Ngũ Giảng Viên",
    sectionDescription:
        "Giảng Viên Được Đào Tạo Bài Bản, Tận Tâm Trong Công Việc.",
    teamsData: [
        {
            Image: require("../../images/thaytien1.jpg"),
            Name: "Thầy Tiền",
            Profession: "Giảng Viên 2D ,3D",
            facebookLink: "https://www.facebook.com/tiencanh.chonphieubong",
            linkedinLink: "/#0",
            twitterLink: "/#0",
        },
        {
            Image: require("../../images/thaytien1.jpg"),
            Name: "Thầy Tiền",
            Profession: "Giảng Viên 2D ,3D",
            facebookLink: "https://www.facebook.com/tiencanh.chonphieubong",
            linkedinLink: "/#0",
            twitterLink: "/#0",
        },
        {
            Image: require("../../images/thaytien1.jpg"),
            Name: "Thầy Tiền",
            Profession: "Giảng Viên 2D ,3D",
            facebookLink: "https://www.facebook.com/tiencanh.chonphieubong",
            linkedinLink: "/#0",
            twitterLink: "/#0",
        },
        {
            Image: require("../../images/thaytien1.jpg"),
            Name: "Thầy Tiền",
            Profession: "Giảng Viên 2D ,3D",
            facebookLink: "https://www.facebook.com/tiencanh.chonphieubong",
            linkedinLink: "/#0",
            twitterLink: "/#0",
        },

    ]
};
